	public static String encodeBase64(String str) {
		if(StringUtil.isEmpty(str)) return "";
		BASE64Encoder encoder = new BASE64Encoder();
		try {
			return encoder.encode(str.getBytes("UTF-8"));
		} catch (UnsupportedEncodingException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		String encodeStr = new sun.misc.BASE64Encoder().encode(str.getBytes());
		encodeStr = encodeStr.replaceAll(" ", "").replaceAll("\\n", "").replaceAll("\\t", "").replaceAll("\\s", "");
		return encodeStr;
	}

	// 鎬�,寰呮暣
	public static String encrypt(String data) {
		return encrypt(data, Constants.DES_KEY);
	}

	// 鎬�,寰呮暣
	public static String encrypt(String data, String key) {
		byte[] bt = null;
		try {
			bt = encrypt(data.getBytes("UTF-8"), key.getBytes());
		} catch (UnsupportedEncodingException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (Exception e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		String strs = new BASE64Encoder().encode(bt);
		return Base64Utils.encodeToString(bt);
	}

	// 鎬�,寰呮暣
	private static byte[] encrypt(byte[] data, byte[] key) throws Exception {
		SecureRandom sr = new SecureRandom();

		DESKeySpec dks = new DESKeySpec(key);

		SecretKeyFactory keyFactory = SecretKeyFactory.getInstance("DES");
		SecretKey securekey = keyFactory.generateSecret(dks);

		Cipher cipher = Cipher.getInstance("DES");

		cipher.init(1, securekey, sr);
		return cipher.doFinal(data);
	}

